import cloudy from './img/cloudy.jpg';
import TextField from '@mui/material/TextField';
import { useState ,useEffect} from 'react'


const WeatherApp = () => {

    //month
    const month = new Array();
    month[0] = "January";
    month[1] = "February";
    month[2] = "March";
    month[3] = "April";
    month[4] = "May";
    month[5] = "June";
    month[6] = "July";
    month[7] = "August";
    month[8] = "September";
    month[9] = "October";
    month[10] = "November";
    month[11] = "December";

    //day
    const day = new Array();
    day[0]="Monday"
    day[1]="Tuesday"
    day[2]="Wednesday"
    day[3]="Thursday"
    day[4]="Friday"
    day[5]="Saturday"
    day[6]="Sunday"
    
    let today = new Date();
    let name = month[today.getMonth()];
    let dayName = day[today.getDay()];
    
    var date = (dayName) + ", " + (name) + " " + today.getDate() + ", " + today.getFullYear();
    



    //Api
