            const response = await fetch(url)
            const resJson = await response.json()
            console.log(resJson)
        }

        fetchApi()
    })
    
    return (


        <div className="main-div" style={{ background: `linear-gradient(rgba(255,255,255,.2), rgba(255,255,255,.2)), url(${cloudy})` }}>
            <h1>My Weather App </h1>
            <center>

                <div className="myComponents"   >
                    <TextField onChange={(e) => setCityOrCountry(e.target.value)} className="input-field" id="standard-basic" label="Enter your country/city . . ." variant="standard" />
