                    <p className="date">{date}</p>
                    <h1 className="temp">32 &deg; C</h1>
                    <p className="design">---------------------</p>
                    <h4 className="atmosphere">Cloudy</h4>

                </div>
            </center>
        </div>

    )
}


export default WeatherApp
