import React from 'react'
import cloudy from './img/cloudy.jpg';
import TextField from '@mui/material/TextField';
import { useState, useEffect } from 'react'


const WeatherApp = () => {

    //month
    const month = new Array();
    month[0] = "January";
    month[1] = "February";
    month[2] = "March";
    month[3] = "April";
    month[4] = "May";
    month[5] = "June";
    month[6] = "July";
    month[7] = "August";
    month[8] = "September";
    month[9] = "October";
    month[10] = "November";
    month[11] = "December";

    //day
    const day = new Array();
    day[0] = "Monday"
    day[1] = "Tuesday"
    day[2] = "Wednesday"
    day[3] = "Thursday"
    day[4] = "Friday"
    day[5] = "Saturday"
    day[6] = "Sunday"

    let today = new Date();
    let name = month[today.getMonth()];
    let dayName = day[today.getDay()];

    var date = (dayName) + ", " + (name) + " " + today.getDate() + ", " + today.getFullYear();


    var atm;

    //Api
    const [cityData, setCityData] = useState()
    const [city, setCity] = useState("Karachi")

    useEffect(() => {
        const fetchApi = async () => {

            const axios = require('axios');

            axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=ad9da9365f7fb965f86f40daa394fe46`)
            .then(function (response) {
                // handle success
                console.log(response);
              })
              .catch(function (error) {
                // handle error
                console.log(error);
              })
              .then(function () {
                // always executed
              });
            // const response = await fetch(url)
            // const resJson = await response.json()
            //  setCityData(resJson.weather[0].main)
            // console.log(resJson.weather[0].main)

        }

        fetchApi()

        
    }, [city])




    return (


        <div className="main-div" style={{ background: `linear-gradient(rgba(255,255,255,.2), rgba(255,255,255,.2)), url(${cloudy})` }}>
             <h1>My Weather App </h1>
            <center>

               <div className="myComponents"   >
                     <TextField onChange={(e) => setCity(e.target.value)} className="input-field" id="standard-basic" label="Enter your country/city . . ." variant="standard" />

                    {!cityData ? (
                        <p className="ErrorMsg">Sorry! No data found</p>
                    ) : (
                        
                        <>
                            <p className="countryName">{city}</p>
                            <p className="date">{date}</p>
                            <h1 className="temp">{cityData.temp} &deg; C</h1>
                            <p className="design">---------------------</p>
                            {/* <h4 className="atmosphere">{cityData.weather[0].main}</h4> */}
                            <h4>Feels like {cityData.feels_like}</h4>
                            <p>{cityData.temp_min} &deg;C / {cityData.temp_max}&deg;C </p>
                        </>
                    )

                    }


              </div>
          </center>
         </div>

     )
 }


export default WeatherApp;
