import React from 'react'
import cloudy from './img/cloudy.jpg';
import TextField from '@mui/material/TextField';
import {useState} from 'react'


const WeatherApp = () => {

//date
const month = new Array();
month[0] = "January";
month[1] = "February";
month[2] = "March";
month[3] = "April";
month[4] = "May";
month[5] = "June";
month[6] = "July";
month[7] = "August";
month[8] = "September";
month[9] = "October";
month[10] = "November";
month[11] = "December";
    const [cityOrcountry, setCityOrCountry] = useState("Karachi")

    let today = new Date();
    let name = month[today.getMonth()];
    var date = (name) + ", " + today.getDate() + " " +today.getFullYear();

    return (


        <div className="main-div" style={{ background: `linear-gradient(rgba(255,255,255,.2), rgba(255,255,255,.2)), url(${cloudy})` }}>
            <h1>My Weather App </h1>
            <center>

                <div className="myComponents"   >
                    <TextField onChange={(e)=>setCityOrCountry(e.target.value)} className="input-field" id="standard-basic" label="Enter your country/city . . ." variant="standard" />
                    <p className="countryName">{cityOrcountry}</p>
                    <p className="date">{date}</p>
                    <h1 className="temp">32 &deg; C</h1>
                    <p className="design">---------------------</p>
                    <h4 className="atmosphere">Cloudy</h4>

                </div>
            </center>
        </div>

    )
}


export default WeatherApp
